// src/pages/Signup.js
import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';

function Signup() {
  const { signup } = useAuth();
  const [formData, setFormData] = useState({ username: '', password: '' });

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    signup(formData);
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <form onSubmit={handleSubmit} className="w-full max-w-md p-6 bg-white rounded-md shadow">
        <h2 className="text-2xl font-semibold text-center mb-6">Sign Up</h2>
        <input type="text" name="username" value={formData.username} onChange={handleChange} className="mb-4 p-2 w-full border rounded" placeholder="Username" required />
        <input type="password" name="password" value={formData.password} onChange={handleChange} className="mb-4 p-2 w-full border rounded" placeholder="Password" required />
        <button type="submit" className="w-full py-2 px-4 bg-blue-500 text-white rounded">Sign Up</button>
      </form>
    </div>
  );
}

export default Signup;
