// src/components/CarForm.js
import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import api from '../api';

function CarForm({ isEdit }) {
  const [formData, setFormData] = useState({ title: '', description: '', tags: '' });
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    if (isEdit) {
      const fetchCar = async () => {
        const response = await api.get(`/cars/${id}`);
        setFormData(response.data);
      };
      fetchCar();
    }
  }, [isEdit, id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isEdit) {
        await api.put(`/cars/${id}`, formData);
      } else {
        await api.post('/cars', formData);
      }
      navigate('/cars');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="title" placeholder="Title" value={formData.title} onChange={(e) => setFormData({ ...formData, title: e.target.value })} />
      <textarea name="description" placeholder="Description" value={formData.description} onChange={(e) => setFormData({ ...formData, description: e.target.value })} />
      <input type="text" name="tags" placeholder="Tags" value={formData.tags} onChange={(e) => setFormData({ ...formData, tags: e.target.value })} />
      <button type="submit">{isEdit ? 'Update Car' : 'Add Car'}</button>
    </form>
  );
}

export default CarForm;
