// src/pages/ProductCreate.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

function ProductCreate() {
  const { user } = useAuth();
  const [formData, setFormData] = useState({ title: '', description: '' });
  const navigate = useNavigate();

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post('http://localhost:5000/api/cars', formData, {
      headers: { Authorization: `Bearer ${user.token}` }
    });
    navigate('/products');
  };

  return (
    <form onSubmit={handleSubmit} className="p-4">
      <input type="text" name="title" placeholder="Title" onChange={handleChange} className="w-full mb-4 p-2 border rounded" />
      <textarea name="description" placeholder="Description" onChange={handleChange} className="w-full mb-4 p-2 border rounded"></textarea>
      <button type="submit" className="w-full py-2 px-4 bg-blue-500 text-white rounded">Create Product</button>
    </form>
  );
}

export default ProductCreate;
