import axios from "axios";

// Set up base URL for API calls
const API_URL = "http://localhost:5000/api";

// Axios instance to handle auth tokens and API requests
const api = axios.create({
  baseURL: API_URL,
});

// Add a request interceptor to add token in headers for authenticated requests
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem("authToken");
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

export const createUser = (userData) => api.post("/auth/signup", userData);
export const loginUser = (credentials) => api.post("/auth/login", credentials);
export const createProduct = (productData) => api.post("/cars", productData);
export const getUserProducts = () => api.get("/cars");
export const getProduct = (id) => api.get(`/cars/${id}`);
export const updateProduct = (id, productData) => api.put(`/cars/${id}`, productData);
export const deleteProduct = (id) => api.delete(`/cars/${id}`);
export const searchProducts = (query) => api.get(`/cars/search?q=${query}`);
